"use strict";(self.webpackChunkwiki=self.webpackChunkwiki||[]).push([[3633],{8678:i=>{i.exports=JSON.parse('{"tag":{"label":"licensing","permalink":"/tags/licensing","allTagsPath":"/tags","count":3,"items":[{"id":"music-publishing/film-tv","title":"Film and TV Admin Highlights","description":"The music business and publishing industry plays a critical role in film and television administration, bridging creative expression with legal frameworks and revenue generation. This symbiotic relationship ensures composers, songwriters, and rights holders are compensated while enabling filmmakers to enhance storytelling through music. Below is a brief overview of this topic.","permalink":"/film-tv"},{"id":"music-publishing/royalties/mechanical-royalties","title":"Mechanical Royalties","description":"Mechanical royalties are payments to songwriters and publishers whenever a song is reproduced or distributed \u2014 digitally or physically. They are distinct from performance royalties, and stem from the mechanical right, one of the six exclusive rights under copyright law.","permalink":"/music-publishing/royalties/mechanical-royalties"},{"id":"music-publishing/pro/performing-rights-organizations","title":"Performing Rights Organizations Overview","description":"Performing Rights Organizations (PROs) are entities that help songwriters, composers, and music publishers get paid when their music is publicly performed. This includes performances on radio, television, streaming services, live concerts, and even in businesses like restaurants or retail stores.","permalink":"/music-publishing/pro/overview"}],"unlisted":false}}')}}]);