"use strict";(self.webpackChunkwiki=self.webpackChunkwiki||[]).push([[4531],{4854:e=>{e.exports=JSON.parse('{"tag":{"label":"metadata","permalink":"/tags/metadata","allTagsPath":"/tags","count":1,"items":[{"id":"music-publishing/technology/cwr-101","title":"CWR 101","description":"Common Works Registration (CWR) is the global standard for batch registering musical works with Collective Management Organizations (CMOs) outside the US and Canada. In North America, most publishers use Electronic Batch Registration (EBR) files\u2014simple .xls spreadsheets\u2014accepted by PROs like ASCAP, BMI, and SOCAN. However, CMOs abroad require CWR files: specially formatted text files that follow a strict, but inconsistently implemented, specification.","permalink":"/music-publishing/technology/cwr-101"}],"unlisted":false}}')}}]);